import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './OrangeAssistant.css';

const OrangeAssistant = ({ currentData, allData, selectedMonth }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationHistory, setConversationHistory] = useState([]);
  const [userPreferences, setUserPreferences] = useState({});
  const [dataInsights, setDataInsights] = useState([]);
  const messagesEndRef = useRef(null);

  // Âä†ËΩΩÂéÜÂè≤ËÆ∞ÂøÜ
  useEffect(() => {
    console.log('üçä Â∞èÊ©ôÂ≠êÁªÑ‰ª∂Â∑≤Âä†ËΩΩ');
    loadMemory();
  }, []);

  // ‰øùÂ≠òÂØπËØùÂéÜÂè≤Âà∞localStorage
  useEffect(() => {
    if (messages.length > 0) {
      try {
        // ‰øùÂ≠òÂØπËØùÂéÜÂè≤
        localStorage.setItem('orangeAssistant_history', JSON.stringify(conversationHistory));
        
        // ‰øùÂ≠òÁî®Êà∑ÂÅèÂ•Ω
        localStorage.setItem('orangeAssistant_preferences', JSON.stringify(userPreferences));
        
        // ‰øùÂ≠òÊï∞ÊçÆÊ¥ûÂØü
        localStorage.setItem('orangeAssistant_insights', JSON.stringify(dataInsights));
      } catch (error) {
        console.error('‰øùÂ≠òËÆ∞ÂøÜÂ§±Ë¥•:', error);
      }
    }
  }, [messages, userPreferences, dataInsights, conversationHistory]);

  const loadMemory = () => {
    try {
      // Âä†ËΩΩÂØπËØùÂéÜÂè≤
      const savedHistory = localStorage.getItem('orangeAssistant_history');
      if (savedHistory) {
        const history = JSON.parse(savedHistory);
        setConversationHistory(history.slice(-20)); // ‰øùÁïôÊúÄËøë20Êù°ÂØπËØù
        console.log('üìö Âä†ËΩΩ‰∫Ü', history.length, 'Êù°ÂéÜÂè≤ÂØπËØù');
      }

      // Âä†ËΩΩÁî®Êà∑ÂÅèÂ•Ω
      const savedPreferences = localStorage.getItem('orangeAssistant_preferences');
      if (savedPreferences) {
        setUserPreferences(JSON.parse(savedPreferences));
        console.log('üí° Âä†ËΩΩ‰∫ÜÁî®Êà∑ÂÅèÂ•Ω');
      }

      // Âä†ËΩΩÊï∞ÊçÆÊ¥ûÂØü
      const savedInsights = localStorage.getItem('orangeAssistant_insights');
      if (savedInsights) {
        setDataInsights(JSON.parse(savedInsights));
        console.log('üîç Âä†ËΩΩ‰∫Ü', JSON.parse(savedInsights).length, 'Êù°Êï∞ÊçÆÊ¥ûÂØü');
      }
    } catch (error) {
      console.error('Âä†ËΩΩËÆ∞ÂøÜÂ§±Ë¥•:', error);
    }
  };

  // ÂàÜÊûêÁî®Êà∑ÈóÆÈ¢òÔºåÊèêÂèñÂÖ≥Ê≥®ÁÇπ
  const analyzeUserIntent = (question) => {
    const keywords = {
      'ÊúàÊ¥ª': 'userActivity',
      'Áî®Êà∑': 'userActivity',
      'Ê¥ªË∑É': 'userActivity',
      'Ëê•Êî∂': 'revenue',
      'Êî∂ÂÖ•': 'revenue',
      '‰ªòË¥π': 'revenue',
      'ARPU': 'arpu',
      'ARPPU': 'arppu',
      'ÁïôÂ≠ò': 'retention',
      'ËÆ¢Âçï': 'orders',
      'ËΩ¨Âåñ': 'conversion',
      'Ê∑±Â∫¶ËÆøÈóÆ': 'engagement',
      '‰ΩøÁî®Áéá': 'usage',
      '‰ºöÂëò': 'membership'
    };

    const concerns = [];
    for (const [keyword, category] of Object.entries(keywords)) {
      if (question.includes(keyword)) {
        concerns.push(category);
      }
    }

    return concerns;
  };

  // Êõ¥Êñ∞Áî®Êà∑ÂÅèÂ•Ω
  const updateUserPreferences = (question, concerns) => {
    const newPreferences = { ...userPreferences };
    
    concerns.forEach(concern => {
      newPreferences[concern] = (newPreferences[concern] || 0) + 1;
    });

    // ËÆ∞ÂΩïÊúÄÂêéÊèêÈóÆÊó∂Èó¥
    newPreferences.lastInteraction = new Date().toISOString();
    newPreferences.totalQuestions = (newPreferences.totalQuestions || 0) + 1;

    setUserPreferences(newPreferences);
  };

  // ÊèêÂèñÂπ∂‰øùÂ≠òÊï∞ÊçÆÊ¥ûÂØü
  const extractInsights = (aiResponse, currentMonth) => {
    const newInsights = [...dataInsights];
    
    // ÈôêÂà∂Ê¥ûÂØüÊï∞ÈáèÔºå‰øùÁïôÊúÄËøë50Êù°
    if (newInsights.length >= 50) {
      newInsights.shift();
    }

    newInsights.push({
      month: currentMonth,
      timestamp: new Date().toISOString(),
      content: aiResponse.substring(0, 200), // ‰øùÂ≠òÂâç200Â≠óÁ¨¶
      metrics: currentData ? {
        ÊúàÊ¥ª: currentData.ÊúàÊ¥ª,
        Ëê•Êî∂: currentData.Ëê•Êî∂,
        ÁïôÂ≠ò: currentData.Ê¨°ÊúàÁïôÂ≠ò
      } : null
    });

    setDataInsights(newInsights);
  };

  // ÊûÑÂª∫Â¢ûÂº∫ÁöÑ‰∏ä‰∏ãÊñáÊèêÁ§∫
  const buildEnhancedContext = (userQuestion) => {
    let context = '';

    // Ê∑ªÂä†Áî®Êà∑ÂÅèÂ•Ω‰ø°ÊÅØ
    if (Object.keys(userPreferences).length > 0) {
      const topConcerns = Object.entries(userPreferences)
        .filter(([key]) => key !== 'lastInteraction' && key !== 'totalQuestions')
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([key]) => key);

      if (topConcerns.length > 0) {
        context += `\nÁî®Êà∑ÈÄöÂ∏∏ÂÖ≥Ê≥®Ôºö${topConcerns.join('„ÄÅ')}`;
      }
    }

    // Ê∑ªÂä†ÊúÄËøëÁöÑÂØπËØùÂéÜÂè≤
    if (conversationHistory.length > 0) {
      const recentHistory = conversationHistory.slice(-3);
      context += '\n\nÊúÄËøëÁöÑÂØπËØùÔºö\n';
      recentHistory.forEach(item => {
        context += `Q: ${item.question}\nA: ${item.answer.substring(0, 100)}...\n`;
      });
    }

    // Ê∑ªÂä†Áõ∏ÂÖ≥ÁöÑÂéÜÂè≤Ê¥ûÂØü
    if (dataInsights.length > 0 && selectedMonth) {
      const relevantInsights = dataInsights
        .filter(insight => insight.month === selectedMonth)
        .slice(-2);
      
      if (relevantInsights.length > 0) {
        context += '\n\nÊú¨Êúà‰πãÂâçÁöÑÂàÜÊûêÔºö\n';
        relevantInsights.forEach(insight => {
          context += `- ${insight.content.substring(0, 100)}...\n`;
        });
      }
    }

    return context;
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleOrangeClick = () => {
    setIsOpen(true);
    if (messages.length === 0) {
      // ËÆ°ÁÆóÊàêÈïøÁ≠âÁ∫ß
      const totalQuestions = userPreferences.totalQuestions || 0;
      let level = "Êñ∞Êâã";
      let emoji = "üå±";
      
      if (totalQuestions >= 100) {
        level = "‰∏ìÂÆ∂";
        emoji = "üèÜ";
      } else if (totalQuestions >= 51) {
        level = "‰∏ì‰∏ö";
        emoji = "üíé";
      } else if (totalQuestions >= 11) {
        level = "ÁÜüÊÇâ";
        emoji = "üåü";
      }
      
      // ÁîüÊàê‰∏™ÊÄßÂåñÊ¨¢ËøéÊ∂àÊÅØ
      let welcomeMessage = `ÊÇ®Â•ΩÔºåÊàëÊòØ‰Ω†ÁöÑAIÊï∞ÊçÆÂàÜÊûêÂä©ÊâãÂ∞èÊ©ôÂ≠êÔºÅüçä\n\n`;
      
      // Â¶ÇÊûúÊúâÂéÜÂè≤ËÆ∞ÂΩïÔºåÊòæÁ§∫ÊàêÈïø‰ø°ÊÅØ
      if (totalQuestions > 0) {
        welcomeMessage += `${emoji} ÂΩìÂâçÁ≠âÁ∫ßÔºö${level}Âä©Êâã\n`;
        welcomeMessage += `üí¨ Êàë‰ª¨Â∑≤Áªè‰∫§ÊµÅËøá ${totalQuestions} Ê¨°‰∫ÜÔºÅ\n`;
        welcomeMessage += `üìö ÊàëËÆ∞‰Ωè‰∫Ü ${conversationHistory.length} Êù°ÂØπËØù\n`;
        welcomeMessage += `ÔøΩ ÁßØÁ¥Ø‰∫Ü ${dataInsights.length} Êù°Êï∞ÊçÆÊ¥ûÂØü\n\n`;
        welcomeMessage += `ÈÄöËøá‰∏çÊñ≠Â≠¶‰π†ÔºåÊàëÂØπÊÇ®ÁöÑÈúÄÊ±ÇÊúâ‰∫ÜÊõ¥Ê∑±ÁöÑ‰∫ÜËß£„ÄÇ\n\n`;
      }

      welcomeMessage += 'ÊàëÂèØ‰ª•Â∏ÆÂä©ÊÇ®Ôºö\n‚Ä¢ ÂàÜÊûêÂΩìÂâçÊúàÂ∫¶Êï∞ÊçÆË°®Áé∞\n‚Ä¢ Ëß£ËØªÊï∞ÊçÆË∂ãÂäøÂíåÂèòÂåñ\n‚Ä¢ Êèê‰æõ‰∏öÂä°‰ºòÂåñÂª∫ËÆÆ\n‚Ä¢ ÂõûÁ≠îÊï∞ÊçÆÁõ∏ÂÖ≥ÈóÆÈ¢ò\n\n';

      // Âü∫‰∫éÁî®Êà∑ÂÅèÂ•ΩÊèê‰æõÂª∫ËÆÆ
      if (Object.keys(userPreferences).length > 2) {
        const topConcerns = Object.entries(userPreferences)
          .filter(([key]) => key !== 'lastInteraction' && key !== 'totalQuestions')
          .sort((a, b) => b[1] - a[1])
          .slice(0, 3);
        
        if (topConcerns.length > 0) {
          const concernMap = {
            'userActivity': 'Áî®Êà∑Ê¥ªË∑ÉÂ∫¶',
            'revenue': 'Ëê•Êî∂Ë°®Áé∞',
            'retention': 'Áî®Êà∑ÁïôÂ≠ò',
            'arpu': 'ARPUÊåáÊ†á',
            'arppu': 'ARPPUÊåáÊ†á',
            'orders': 'ËÆ¢ÂçïÊï∞ÊçÆ',
            'engagement': 'Áî®Êà∑ÂèÇ‰∏éÂ∫¶',
            'usage': '‰ΩøÁî®Áéá',
            'membership': '‰ºöÂëòÊï∞ÊçÆ'
          };
          
          const topConcernNames = topConcerns
            .map(([key]) => concernMap[key])
            .filter(Boolean);
          
          if (topConcernNames.length > 0) {
            welcomeMessage += `üí° ÊàëÊ≥®ÊÑèÂà∞ÊÇ®ÁªèÂ∏∏ÂÖ≥Ê≥®Ôºö${topConcernNames.join('„ÄÅ')}\n\n`;
          }
        }
      }
      
      welcomeMessage += 'Êúâ‰ªÄ‰πàÈúÄË¶ÅÊàëÂ∏ÆÂä©ÁöÑÂêóÔºü';

      setMessages([{
        type: 'assistant',
        content: welcomeMessage,
        timestamp: new Date()
      }]);
    }
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userQuestion = inputValue;
    const userMessage = {
      type: 'user',
      content: userQuestion,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // 1. ÂàÜÊûêÁî®Êà∑ÊÑèÂõæ
      const concerns = analyzeUserIntent(userQuestion);
      
      // 2. Êõ¥Êñ∞Áî®Êà∑ÂÅèÂ•Ω
      updateUserPreferences(userQuestion, concerns);
      
      // 3. ÊûÑÂª∫Â¢ûÂº∫‰∏ä‰∏ãÊñá
      const enhancedContext = buildEnhancedContext(userQuestion);
      
      // 4. ÊûÑÂª∫ÂÆåÊï¥ÊèêÁ§∫ËØç
      const contextInfo = currentData ? `
ÂΩìÂâçÊï∞ÊçÆÊ¶ÇÂÜµÔºà${selectedMonth ? selectedMonth.replace('-', 'Âπ¥') + 'Êúà' : ''}ÔºâÔºö
- ÊúàÊ¥ªÁî®Êà∑Ôºö${currentData.ÊúàÊ¥ª?.toLocaleString() || 'N/A'}
- Ê¨°ÊúàÁïôÂ≠òÁéáÔºö${currentData.Ê¨°ÊúàÁïôÂ≠ò || 'N/A'}%
- Ëê•Êî∂Ôºö${currentData.Ëê•Êî∂?.toLocaleString() || 'N/A'}
- ËÆ¢ÂçïÊï∞Ôºö${currentData.ËÆ¢Âçï?.toLocaleString() || 'N/A'}
- ARPUÔºö${currentData.ARPU || 'N/A'}
- ARPPUÔºö${currentData.ARPPU || 'N/A'}
- Ê∑±Â∫¶ËÆøÈóÆÁéáÔºö${currentData.Ê∑±Â∫¶ËÆøÈóÆÁéá || 'N/A'}%
- ‰ΩøÁî®ÁéáÔºö${currentData.‰ΩøÁî®Áéá || 'N/A'}%
- Â§ß‰ºöÂëòÊ¥ªË∑ÉÁéáÔºö${currentData.Â§ß‰ºöÂëòÊ¥ªË∑ÉÁéá || 'N/A'}%
` : 'ÊöÇÊó†ÂΩìÂâçÊúà‰ªΩÊï∞ÊçÆ';

      const prompt = `
‰Ω†ÊòØÂ∞èÊ©ôÂ≠êÔºå‰∏Ä‰∏™‰∏ì‰∏öÁöÑÊï∞ÊçÆÂàÜÊûêÂä©Êâã„ÄÇËØ∑Âü∫‰∫é‰ª•‰∏ãÊï∞ÊçÆÂõûÁ≠îÁî®Êà∑ÈóÆÈ¢òÔºö

${contextInfo}

${enhancedContext}

Áî®Êà∑ÈóÆÈ¢òÔºö${userQuestion}

ËØ∑‰ª•ÂèãÂ•Ω„ÄÅ‰∏ì‰∏öÁöÑËØ≠Ê∞îÂõûÁ≠îÔºåÈáçÁÇπÂÖ≥Ê≥®Êï∞ÊçÆÊ¥ûÂØüÂíåÂÆûÁî®Âª∫ËÆÆ„ÄÇÂõûÁ≠îË¶ÅÁÆÄÊ¥ÅÊòé‰∫ÜÔºå‰∏çË∂ÖËøá200Â≠ó„ÄÇ
`;

      // 5. Ë∞ÉÁî®AI API
      const response = await axios.post('https://api.deepseek.com/v1/chat/completions', {
        model: 'deepseek-chat',
        messages: [
          {
            role: 'system',
            content: '‰Ω†ÊòØÂ∞èÊ©ôÂ≠êÔºå‰∏Ä‰∏™‰∏ì‰∏öÂèãÂ•ΩÁöÑÊï∞ÊçÆÂàÜÊûêÂä©Êâã„ÄÇÂõûÁ≠îË¶ÅÁÆÄÊ¥ÅÂÆûÁî®ÔºåËØ≠Ê∞î‰∫≤Âàá‰∏ì‰∏ö„ÄÇ'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: 300
      }, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      const aiResponse = response.data.choices[0].message.content;
      
      // 6. ‰øùÂ≠òÂØπËØùÂéÜÂè≤
      const newConversation = {
        question: userQuestion,
        answer: aiResponse,
        timestamp: new Date().toISOString(),
        month: selectedMonth
      };
      
      const updatedHistory = [...conversationHistory, newConversation];
      if (updatedHistory.length > 20) {
        updatedHistory.shift(); // ‰øùÊåÅÊúÄÂ§ö20Êù°
      }
      setConversationHistory(updatedHistory);
      
      // 7. ÊèêÂèñÂπ∂‰øùÂ≠òÊï∞ÊçÆÊ¥ûÂØü
      extractInsights(aiResponse, selectedMonth);
      
      // 8. ÊòæÁ§∫AIÂõûÁ≠î
      const assistantMessage = {
        type: 'assistant',
        content: aiResponse,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('AIÂØπËØùÂ§±Ë¥•:', error);
      
      const errorMessage = {
        type: 'assistant',
        content: 'Êä±Ê≠âÔºåÊàëÁé∞Âú®Êó†Ê≥ïÂõûÁ≠îÊÇ®ÁöÑÈóÆÈ¢ò„ÄÇËØ∑Á®çÂêéÂÜçËØïÔºåÊàñËÄÖÊ£ÄÊü•APIÈÖçÁΩÆÊòØÂê¶Ê≠£Á°Æ„ÄÇ',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (timestamp) => {
    return timestamp.toLocaleTimeString('zh-CN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <>
      {/* Â∞èÊ©ôÂ≠êÂõæÊ†á */}
      <div className="orange-assistant-icon" onClick={handleOrangeClick}>
        <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠êAIÂä©Êâã" className="orange-image" />
        <div className="orange-tooltip">ÁÇπÂáª‰∏éÂ∞èÊ©ôÂ≠êÂØπËØù</div>
      </div>

      {/* ÂØπËØùÁ™óÂè£ */}
      {isOpen && (
        <div className="orange-chat-overlay">
          <div className="orange-chat-window">
            <div className="orange-chat-header">
              <div className="orange-chat-title">
                <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠ê" className="orange-avatar" />
                <span>Â∞èÊ©ôÂ≠ê AI Êï∞ÊçÆÂàÜÊûêÂä©Êâã</span>
              </div>
              <button className="orange-close-btn" onClick={handleClose}>√ó</button>
            </div>

            <div className="orange-chat-messages">
              {messages.map((message, index) => (
                <div key={index} className={`orange-message ${message.type}`}>
                  {message.type === 'assistant' && (
                    <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠ê" className="message-avatar" />
                  )}
                  <div className="message-content">
                    <div className="message-text">{message.content}</div>
                    <div className="message-time">{formatTime(message.timestamp)}</div>
