import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AIAnalysis.css';

const AIAnalysis = ({ currentData, allData, selectedMonth }) => {
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (currentData && allData.length > 0) {
      generateAnalysis();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentData, selectedMonth]);

  const generateAnalysis = async () => {
    setLoading(true);
    setError('');

    try {
      // 暂时使用静态分析，避免API调用
      const fallbackAnalysis = generateFallbackAnalysis();
      
      // 模拟API延迟
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setAnalysis(fallbackAnalysis);
    } catch (err) {
      console.error('分析失败:', err);
      setError('分析服务暂时不可用，请稍后重试');
    } finally {
      setLoading(false);
    }
  };

  const generateFallbackAnalysis = () => {
    if (!currentData) return { keyChanges: '', trends: '', suggestions: '' };

    const [currentYear, currentMonth] = selectedMonth.split('-').map(Number);
    const lastMonthData = allData.find(row => {
      if (currentMonth === 1) {
        return row.年份 === currentYear - 1 && row.月份 === 12;
      }
      return row.年份 === currentYear && row.月份 === currentMonth - 1;
    });

    let keyChanges = `本月月活用户达到${currentData.月活.toLocaleString()}，`;
    let trends = `从数据表现看，`;
    let suggestions = `针对当前数据表现，建议：`;
    
    if (lastMonthData) {
      const userChange = ((currentData.月活 - lastMonthData.月活) / lastMonthData.月活 * 100).toFixed(2);
      const revenueChange = ((currentData.营收 - lastMonthData.营收) / lastMonthData.营收 * 100).toFixed(2);
      
      keyChanges += `环比${userChange > 0 ? '增长' : '下降'}${Math.abs(userChange)}%。营收${revenueChange > 0 ? '增长' : '下降'}${Math.abs(revenueChange)}%。`;
    }
    
    if (currentData.次月留存 >= 40) {
      trends += `用户留存率${currentData.次月留存}%表现良好，`;
    } else {
      trends += `用户留存率${currentData.次月留存}%需要关注，`;
    }
    
    if (currentData.使用率 >= 70) {
      trends += `使用率${currentData.使用率}%维持高位。`;
    } else {
      trends += `使用率${currentData.使用率}%有提升空间。`;
    }
    
    suggestions = `• 加强内容运营和用户互动机制\n• 优化产品功能，提升用户使用深度\n• 通过精准营销提升付费转化率`;

    return { keyChanges, trends, suggestions };
  };

  if (!currentData) return null;

  return (
    <div className="ai-analysis">
      <div className="card">
        <h2 className="ai-header">AI 数据分析</h2>
        
        <div className="analysis-content">
          {loading && (
            <div className="analysis-loading">
              <div className="loading-spinner"></div>
              <p>AI正在分析数据...</p>
            </div>
          )}
          
          {error && (
            <div className="analysis-error">
              <p>{error}</p>
            </div>
          )}
          
          {analysis && !loading && (
            <div className="analysis-grid">
              <div className="analysis-section">
                <div className="analysis-section-header">
                  <div className="analysis-section-icon"></div>
                  <h3 className="analysis-section-title">核心表现</h3>
                </div>
                <div className="analysis-section-content">
                  <div>活跃用户</div>
                  <div style={{fontSize: '20px', fontWeight: '600', color: '#FF6B35', margin: '4px 0'}}>
                    {currentData.月活.toLocaleString()}
                  </div>
                  <div>营收</div>
                  <div style={{fontSize: '20px', fontWeight: '600', color: '#FF6B35', margin: '4px 0'}}>
                    {(currentData.营收 / 10000).toFixed(1)}万
                  </div>
                </div>
              </div>
              
              <div className="analysis-section">
                <div className="analysis-section-header">
                  <div className="analysis-section-icon"></div>
                  <h3 className="analysis-section-title">关键变化</h3>
                </div>
                <div className="analysis-section-content">
                  <ul>
                    <li>用户增长{((currentData.月活 - (allData[allData.length-2]?.月活 || currentData.月活)) / (allData[allData.length-2]?.月活 || 1) * 100).toFixed(1)}%</li>
                    <li>营收下降{Math.abs(((currentData.营收 - (allData[allData.length-2]?.营收 || currentData.营收)) / (allData[allData.length-2]?.营收 || 1) * 100)).toFixed(1)}%</li>
                    <li>留存率表现良好</li>
                  </ul>
                </div>
              </div>
              
              <div className="analysis-section">
                <div className="analysis-section-header">
                  <div className="analysis-section-icon"></div>
                  <h3 className="analysis-section-title">策略建议</h3>
                </div>
                <div className="analysis-section-content">
                  <ul>
                    <li>提升用户粘性，扩大规模</li>
                    <li>优化付费转化机制</li>
                    <li>加强高价值用户运营</li>
                    <li>提高用户使用深度</li>
                  </ul>
                </div>
              </div>
            </div>
          )}
        </div>
        
        <div className="analysis-footer">
          <button 
            onClick={generateAnalysis} 
            disabled={loading}
            className="refresh-btn"
          >
            {loading ? '分析中...' : '重新分析'}
          </button>
          <span className="analysis-note">
            由 DeepSeek AI 提供分析支持
          </span>
        </div>
      </div>
    </div>
  );
};

export default AIAnalysis;
