import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './OrangeAssistant.css';

const OrangeAssistant = ({ currentData, allData, selectedMonth }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleOrangeClick = () => {
    setIsOpen(true);
    if (messages.length === 0) {
      // ÂàùÂßãÊ¨¢ËøéÊ∂àÊÅØ
      setMessages([{
        type: 'assistant',
        content: 'ÊÇ®Â•ΩÔºåÊàëÊòØ‰Ω†ÁöÑAIÊï∞ÊçÆÂàÜÊûêÂä©ÊâãÂ∞èÊ©ôÂ≠êÔºÅüçä\n\nÊàëÂèØ‰ª•Â∏ÆÂä©ÊÇ®Ôºö\n‚Ä¢ ÂàÜÊûêÂΩìÂâçÊúàÂ∫¶Êï∞ÊçÆË°®Áé∞\n‚Ä¢ Ëß£ËØªÊï∞ÊçÆË∂ãÂäøÂíåÂèòÂåñ\n‚Ä¢ Êèê‰æõ‰∏öÂä°‰ºòÂåñÂª∫ËÆÆ\n‚Ä¢ ÂõûÁ≠îÊï∞ÊçÆÁõ∏ÂÖ≥ÈóÆÈ¢ò\n\nÊúâ‰ªÄ‰πàÈúÄË¶ÅÊàëÂ∏ÆÂä©ÁöÑÂêóÔºü',
        timestamp: new Date()
      }]);
    }
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = {
      type: 'user',
      content: inputValue,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // ÊûÑÂª∫‰∏ä‰∏ãÊñá‰ø°ÊÅØ
      const contextInfo = currentData ? `
ÂΩìÂâçÊï∞ÊçÆÊ¶ÇÂÜµÔºà${selectedMonth ? selectedMonth.replace('-', 'Âπ¥') + 'Êúà' : ''}ÔºâÔºö
- ÊúàÊ¥ªÁî®Êà∑Ôºö${currentData.ÊúàÊ¥ª?.toLocaleString() || 'N/A'}
- Ê¨°ÊúàÁïôÂ≠òÁéáÔºö${currentData.Ê¨°ÊúàÁïôÂ≠ò || 'N/A'}%
- Ëê•Êî∂Ôºö${currentData.Ëê•Êî∂?.toLocaleString() || 'N/A'}
- ËÆ¢ÂçïÊï∞Ôºö${currentData.ËÆ¢Âçï?.toLocaleString() || 'N/A'}
- ARPUÔºö${currentData.ARPU || 'N/A'}
- ARPPUÔºö${currentData.ARPPU || 'N/A'}
- Ê∑±Â∫¶ËÆøÈóÆÁéáÔºö${currentData.Ê∑±Â∫¶ËÆøÈóÆÁéá || 'N/A'}%
- ‰ΩøÁî®ÁéáÔºö${currentData.‰ΩøÁî®Áéá || 'N/A'}%
- Â§ß‰ºöÂëòÊ¥ªË∑ÉÁéáÔºö${currentData.Â§ß‰ºöÂëòÊ¥ªË∑ÉÁéá || 'N/A'}%
` : 'ÊöÇÊó†ÂΩìÂâçÊúà‰ªΩÊï∞ÊçÆ';

      const prompt = `
‰Ω†ÊòØÂ∞èÊ©ôÂ≠êÔºå‰∏Ä‰∏™‰∏ì‰∏öÁöÑÊï∞ÊçÆÂàÜÊûêÂä©Êâã„ÄÇËØ∑Âü∫‰∫é‰ª•‰∏ãÊï∞ÊçÆÂõûÁ≠îÁî®Êà∑ÈóÆÈ¢òÔºö

${contextInfo}

Áî®Êà∑ÈóÆÈ¢òÔºö${inputValue}

ËØ∑‰ª•ÂèãÂ•Ω„ÄÅ‰∏ì‰∏öÁöÑËØ≠Ê∞îÂõûÁ≠îÔºåÈáçÁÇπÂÖ≥Ê≥®Êï∞ÊçÆÊ¥ûÂØüÂíåÂÆûÁî®Âª∫ËÆÆ„ÄÇÂõûÁ≠îË¶ÅÁÆÄÊ¥ÅÊòé‰∫ÜÔºå‰∏çË∂ÖËøá200Â≠ó„ÄÇ
`;

      const response = await axios.post('https://api.deepseek.com/v1/chat/completions', {
        model: 'deepseek-chat',
        messages: [
          {
            role: 'system',
            content: '‰Ω†ÊòØÂ∞èÊ©ôÂ≠êÔºå‰∏Ä‰∏™‰∏ì‰∏öÂèãÂ•ΩÁöÑÊï∞ÊçÆÂàÜÊûêÂä©Êâã„ÄÇÂõûÁ≠îË¶ÅÁÆÄÊ¥ÅÂÆûÁî®ÔºåËØ≠Ê∞î‰∫≤Âàá‰∏ì‰∏ö„ÄÇ'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: 300
      }, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      const aiResponse = response.data.choices[0].message.content;
      
      const assistantMessage = {
        type: 'assistant',
        content: aiResponse,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('AIÂØπËØùÂ§±Ë¥•:', error);
      
      const errorMessage = {
        type: 'assistant',
        content: 'Êä±Ê≠âÔºåÊàëÁé∞Âú®Êó†Ê≥ïÂõûÁ≠îÊÇ®ÁöÑÈóÆÈ¢ò„ÄÇËØ∑Á®çÂêéÂÜçËØïÔºåÊàñËÄÖÊ£ÄÊü•APIÈÖçÁΩÆÊòØÂê¶Ê≠£Á°Æ„ÄÇ',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatTime = (timestamp) => {
    return timestamp.toLocaleTimeString('zh-CN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <>
      {/* Â∞èÊ©ôÂ≠êÂõæÊ†á */}
      <div className="orange-assistant-icon" onClick={handleOrangeClick}>
        <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠êAIÂä©Êâã" className="orange-image" />
        <div className="orange-tooltip">ÁÇπÂáª‰∏éÂ∞èÊ©ôÂ≠êÂØπËØù</div>
      </div>

      {/* ÂØπËØùÁ™óÂè£ */}
      {isOpen && (
        <div className="orange-chat-overlay">
          <div className="orange-chat-window">
            <div className="orange-chat-header">
              <div className="orange-chat-title">
                <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠ê" className="orange-avatar" />
                <span>Â∞èÊ©ôÂ≠ê AI Êï∞ÊçÆÂàÜÊûêÂä©Êâã</span>
              </div>
              <button className="orange-close-btn" onClick={handleClose}>√ó</button>
            </div>

            <div className="orange-chat-messages">
              {messages.map((message, index) => (
                <div key={index} className={`orange-message ${message.type}`}>
                  {message.type === 'assistant' && (
                    <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠ê" className="message-avatar" />
                  )}
                  <div className="message-content">
                    <div className="message-text">{message.content}</div>
                    <div className="message-time">{formatTime(message.timestamp)}</div>
                  </div>
                </div>
              ))}
              
              {isLoading && (
                <div className="orange-message assistant">
                  <img src="/Â∞èÊ©ôÂ≠ê.png" alt="Â∞èÊ©ôÂ≠ê" className="message-avatar" />
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            <div className="orange-chat-input">
              <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="ËæìÂÖ•ÊÇ®ÁöÑÈóÆÈ¢ò..."
                className="chat-textarea"
                rows="2"
                disabled={isLoading}
              />
              <button 
                onClick={handleSendMessage}
                disabled={!inputValue.trim() || isLoading}
                className="send-button"
              >
                ÂèëÈÄÅ
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default OrangeAssistant;
